import React, { useState, useEffect, useRef } from 'react';
import { db } from '../firebaseConfig'; // Import firestore instance
import { collection, query, orderBy, onSnapshot, addDoc, serverTimestamp } from 'firebase/firestore';

function ChatPage({ onNavigate, user, pageData }) {
    const { conversationId } = pageData;
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const messagesEndRef = useRef(null);

    // Scroll to the bottom of the chat on new messages
    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    // Listen for real-time messages from Firestore
    useEffect(() => {
        if (!conversationId) return;

        const messagesRef = collection(db, 'conversations', conversationId, 'messages');
        const q = query(messagesRef, orderBy('createdAt'));

        const unsubscribe = onSnapshot(q, (querySnapshot) => {
            const msgs = [];
            querySnapshot.forEach((doc) => {
                msgs.push({ id: doc.id, ...doc.data() });
            });
            setMessages(msgs);
        });

        return () => unsubscribe(); // Cleanup listener on component unmount
    }, [conversationId]);

    const handleSendMessage = async (e) => {
        e.preventDefault();
        if (newMessage.trim() === '' || !user) return;

        const messagesRef = collection(db, 'conversations', conversationId, 'messages');
        await addDoc(messagesRef, {
            text: newMessage,
            createdAt: serverTimestamp(),
            uid: user.id.toString(),
            displayName: user.name
        });

        setNewMessage('');
    };

    return (
        <div className="h-screen flex flex-col">
            <header className="bg-white shadow-sm">
                <div className="container mx-auto px-6 py-4 flex items-center">
                    <button onClick={() => onNavigate('inbox')} className="text-purple-600 hover:text-purple-800 font-semibold">&larr; Back to Inbox</button>
                </div>
            </header>

            <main className="flex-1 overflow-y-auto bg-gray-100 p-6">
                <div className="space-y-4">
                    {messages.map(msg => (
                        <div key={msg.id} className={`flex ${Number(msg.uid) === user.id ? 'justify-end' : 'justify-start'}`}>
                            <div className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${Number(msg.uid) === user.id ? 'bg-purple-600 text-white' : 'bg-white'}`}>
                                <p className="text-sm">{msg.text}</p>
                            </div>
                        </div>
                    ))}
                    <div ref={messagesEndRef} />
                </div>
            </main>

            <footer className="bg-white p-4 border-t">
                <form onSubmit={handleSendMessage} className="flex gap-4">
                    <input 
                        type="text"
                        value={newMessage}
                        onChange={(e) => setNewMessage(e.target.value)}
                        placeholder="Type your message..."
                        className="flex-grow w-full p-3 border rounded-lg"
                    />
                    <button type="submit" className="py-3 px-6 bg-purple-600 text-white font-semibold rounded-lg">Send</button>
                </form>
            </footer>
        </div>
    );
}

export default ChatPage;
