import React, { useState, useEffect, useCallback } from 'react';

function InboxPage({ onNavigate, token }) {
    const [conversations, setConversations] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    // Use useCallback to memoize the fetch function so it's stable
    const fetchInbox = useCallback(async () => {
        if (!token) return;
        setIsLoading(true);
        try {
            const response = await fetch('http://localhost:8000/api/inbox', {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            if (!response.ok) throw new Error('Failed to fetch inbox');
            const data = await response.json();
            setConversations(data);
        } catch (error) {
            console.error(error);
        } finally {
            setIsLoading(false);
        }
    }, [token]);

    // This useEffect hook now handles the initial load and re-fetching when the page is shown again
    useEffect(() => {
        // Fetch data on initial load
        fetchInbox();

        // --- THE FIX IS HERE ---
        // Add an event listener for the 'pageshow' event.
        // This event fires every time the page becomes visible, including on back/forward navigation.
        const handlePageShow = (event) => {
            // The `persisted` property is true if the page is from the bfcache
            if (event.persisted) {
                console.log("Page was restored from cache. Re-fetching inbox.");
                fetchInbox();
            }
        };

        window.addEventListener('pageshow', handlePageShow);

        // Cleanup the event listener when the component unmounts
        return () => {
            window.removeEventListener('pageshow', handlePageShow);
        };
    }, [fetchInbox]); // The effect now depends on the stable fetchInbox function

    const handleDelete = async (conversationId, e) => {
        e.stopPropagation(); 

        if (!window.confirm("Are you sure you want to delete this chat from your view?")) {
            return;
        }

        try {
            const response = await fetch(`http://localhost:8000/api/conversations/${conversationId}/hide`, {
                method: 'PUT',
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (!response.ok) throw new Error('Failed to delete conversation');

            setConversations(prev => prev.filter(convo => convo._id !== conversationId));
        } catch (error) {
            console.error("Error deleting chat:", error);
            alert("Could not delete the chat. Please try again.");
        }
    };

    return (
        <div className="min-h-screen bg-gray-100">
            <header className="bg-white shadow-sm">
                <div className="container mx-auto px-6 py-4">
                    <button onClick={() => onNavigate('home')} className="text-purple-600 hover:text-purple-800 font-semibold">&larr; Back to Home</button>
                </div>
            </header>
            <main className="container mx-auto px-6 py-12">
                <h1 className="text-3xl font-bold text-gray-800 mb-8">Your Inbox</h1>
                <div className="bg-white p-6 rounded-lg shadow-lg">
                    {isLoading ? <p>Loading conversations...</p> : (
                        <ul className="space-y-4">
                            {conversations.length > 0 ? conversations.map(convo => (
                                <li key={convo._id} onClick={() => onNavigate('chat', { conversationId: convo._id })}
                                    className="p-4 border rounded-lg hover:bg-gray-50 cursor-pointer flex justify-between items-center group">
                                    <div>
                                        <p className="font-bold">Conversation regarding Listing ID: {convo.listingId}</p>
                                        <p className="text-sm text-gray-500">
                                            Participants: {convo.participants.join(', ')}
                                        </p>
                                        <p className="text-sm text-gray-500 italic mt-1">
                                            Last message on {new Date(convo.updatedAt).toLocaleString()}
                                        </p>
                                    </div>
                                    <button 
                                        onClick={(e) => handleDelete(convo._id, e)}
                                        className="bg-red-500 text-white font-bold py-1 px-3 rounded-lg opacity-0 group-hover:opacity-100 transition-opacity hover:bg-red-600"
                                    >
                                        Delete
                                    </button>
                                </li>
                            )) : <p>You have no conversations.</p>}
                        </ul>
                    )}
                </div>
            </main>
        </div>
    );
}

export default InboxPage;
