import React, { useState, useEffect, useRef } from "react";
import { useParams, useLocation } from "react-router-dom";
import { io } from "socket.io-client";

function ChatPage() {
  const { sellerId } = useParams();
  const location = useLocation();
  const sellerName = location.state?.sellerName || "Seller";

  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const socketRef = useRef(null);
  const typingTimeoutRef = useRef(null);

  const token = localStorage.getItem("token");
  const user = JSON.parse(localStorage.getItem("user"));

  // Connect to socket.io
  useEffect(() => {
    if (!token || !user) return;

    const socket = io("http://localhost:8000", {
      auth: { token }
    });

    socketRef.current = socket;

    socket.emit("join_room", { otherUserId: sellerId });

    socket.on("receive_message", (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    socket.on("user_typing", () => setIsTyping(true));
    socket.on("user_stop_typing", () => setIsTyping(false));

    socket.on("message_status_updated", ({ id, status, deliveredAt, readAt }) => {
      setMessages((prev) =>
        prev.map((m) =>
          m._id === id ? { ...m, status, deliveredAt, readAt } : m
        )
      );
    });

    socket.on("message_status_bulk_update", ({ updatedMessages }) => {
      setMessages((prev) =>
        prev.map((msg) => {
          const update = updatedMessages.find((u) => u.id === msg._id);
          return update ? { ...msg, ...update } : msg;
        })
      );
    });

    // Fetch initial messages from backend (optional API)
    fetch(`http://localhost:8000/api/messages/${sellerId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then((res) => res.json())
      .then((data) => setMessages(data))
      .catch((err) => console.error("Failed to load messages:", err));

    return () => {
      socket.disconnect();
    };
  }, [sellerId, token, user]);

  const handleSend = () => {
    if (!newMessage.trim()) return;

    socketRef.current.emit("send_message", {
      otherUserId: sellerId,
      otherUserName: sellerName,
      text: newMessage
    });

    setNewMessage("");
    socketRef.current.emit("stop_typing", { otherUserId: sellerId });
  };

  const handleTyping = (e) => {
    setNewMessage(e.target.value);

    socketRef.current.emit("typing", { otherUserId: sellerId });

    if (typingTimeoutRef.current) clearTimeout(typingTimeoutRef.current);
    typingTimeoutRef.current = setTimeout(() => {
      socketRef.current.emit("stop_typing", { otherUserId: sellerId });
    }, 2000);
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-100">
      <header className="bg-purple-600 text-white p-4 font-bold">
        Chat with {sellerName} {isTyping && <span className="italic">typing...</span>}
      </header>

      <div className="flex-1 overflow-y-auto p-4">
        {messages.map((msg) => (
          <div
            key={msg._id || Math.random()}
            className={`mb-2 flex ${
              msg.senderId === user.id ? "justify-end" : "justify-start"
            }`}
          >
            <div
              className={`p-2 rounded-lg max-w-xs ${
                msg.senderId === user.id
                  ? "bg-purple-600 text-white"
                  : "bg-white text-gray-800 border"
              }`}
            >
              <p>{msg.text}</p>
              {msg.status === "read" && (
                <small className="block text-xs mt-1">Read</small>
              )}
              {msg.status === "delivered" && (
                <small className="block text-xs mt-1">Delivered</small>
              )}
            </div>
          </div>
        ))}
      </div>

      <div className="p-4 bg-white border-t flex">
        <input
          type="text"
          className="flex-grow border rounded-l-lg p-2"
          placeholder="Type your message..."
          value={newMessage}
          onChange={handleTyping}
        />
        <button
          onClick={handleSend}
          className="bg-purple-600 text-white px-4 rounded-r-lg"
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatPage;
