// Import the functions you need from the SDKs you need
import { initializeApp } from "firebase/app";
import { getAuth, signInWithCustomToken } from "firebase/auth";
import { getFirestore } from "firebase/firestore";

// --- Your web app's Firebase configuration ---
const firebaseConfig = {
  apiKey: "AIzaSyBFrJy7Z-Bpn6JiohfCWw2M4eONQjbB35Y",
  authDomain: "desiconnect-chat.firebaseapp.com",
  projectId: "desiconnect-chat",
  storageBucket: "desiconnect-chat.firebasestorage.app",
  messagingSenderId: "181210720389",
  appId: "1:181210720389:web:35cdadbf4ba432b9e261ec"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
export const auth = getAuth(app);
export const db = getFirestore(app);

// Function to sign in the user with the custom token from our backend
export const signInToFirebase = (firebaseToken) => {
    return signInWithCustomToken(auth, firebaseToken);
};
