import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';

function InboxPage({ token }) {
    const navigate = useNavigate();
    const [conversations, setConversations] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    console.log('%cInboxPage Component Rendered', 'color: blue; font-weight: bold;', {
        isLoading,
        conversationCount: conversations.length
    });

    const fetchInbox = useCallback(async () => {
        if (!token) {
            console.log('%cDEBUG: fetchInbox skipped - no token.', 'color: orange;');
            return;
        }
        setIsLoading(true);
        console.log('%cDEBUG: fetchInbox STARTING...', 'color: green;');
        try {
            const response = await fetch(`http://localhost:8000/api/inbox`);
            if (!response.ok) throw new Error('Failed to fetch inbox');
            const data = await response.json();
            console.log('%cDEBUG: fetchInbox SUCCESS. Data received:', 'color: green;', data);
            setConversations(data);
        } catch (error) {
            console.error('%cDEBUG: fetchInbox FAILED.', 'color: red;', error);
        } finally {
            setIsLoading(false);
        }
    }, [token]);

    useEffect(() => {
        console.log('%cDEBUG: useEffect for fetching has run.', 'color: purple;');
        fetchInbox();
    }, [fetchInbox]);

    const handleDelete = async (conversationId, e) => {
        e.stopPropagation(); 

        console.log(`%cDEBUG: handleDelete CLICKED for convo ID: ${conversationId}`, 'color: red; font-weight: bold;');

        if (!window.confirm("Are you sure you want to delete this chat from your view?")) {
            console.log('%cDEBUG: User cancelled delete.', 'color: orange;');
            return;
        }

        try {
            const response = await fetch(`http://localhost:8000/api/conversations/${conversationId}/hide`, {
                method: 'PUT',
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (!response.ok) throw new Error('Failed to delete conversation on server');

            console.log('%cDEBUG: Server confirmed delete. Updating UI.', 'color: red;');
            setConversations(prev => {
                const newState = prev.filter(convo => convo._id !== conversationId);
                console.log('%cDEBUG: setConversations called. New state count:', 'color: red;', newState.length);
                return newState;
            });
        } catch (error) {
            console.error("Error deleting chat:", error);
            alert("Could not delete the chat. Please try again.");
        }
    };

    return (
        <div className="min-h-screen bg-gray-100">
            <header className="bg-white shadow-sm">
                <div className="container mx-auto px-6 py-4">
                    <button onClick={() => navigate('/')} className="text-purple-600 hover:text-purple-800 font-semibold">&larr; Back to Home</button>
                </div>
            </header>
            <main className="container mx-auto px-6 py-12">
                <h1 className="text-3xl font-bold text-gray-800 mb-8">Your Inbox</h1>
                <div className="bg-white p-6 rounded-lg shadow-lg">
                    {isLoading ? <p>Loading conversations...</p> : (
                        <ul className="space-y-4">
                            {conversations.map(convo => (
                                <li key={convo._id} onClick={() => navigate(`/inbox/${convo._id}`)}
                                    className="p-4 border rounded-lg hover:bg-gray-50 cursor-pointer flex justify-between items-center group">
                                    <div>
                                        <p>Conversation ID: {convo._id}</p>
                                    </div>
                                    <button 
                                        onClick={(e) => handleDelete(convo._id, e)}
                                        className="bg-red-500 text-white font-bold py-1 px-3 rounded-lg opacity-100"
                                    >
                                        Delete
                                    </button>
                                </li>
                            )) }
                        </ul>
                    )}
                </div>
            </main>
        </div>
    );
}

export default InboxPage;
