import React, { useState, useEffect } from 'react';

function InboxPage({ onNavigate, token, user }) {
    const [conversations, setConversations] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const fetchConversations = async () => {
            if (!token) return;
            setIsLoading(true);
            try {
                const response = await fetch('http://localhost:8000/api/conversations', {
                    headers: { 'Authorization': `Bearer ${token}` }
                });
                if (!response.ok) throw new Error('Failed to fetch conversations');
                const data = await response.json();
                setConversations(data);
            } catch (error) {
                console.error("Error fetching conversations:", error);
            } finally {
                setIsLoading(false);
            }
        };
        fetchConversations();
    }, [token]);

    const getOtherParticipantName = (convo) => {
        const otherParticipantId = Object.keys(convo.participantDetails).find(id => Number(id) !== user.id);
        return otherParticipantId ? convo.participantDetails[otherParticipantId].name : 'Unknown User';
    };

    return (
        <div className="min-h-screen bg-gray-100">
            <header className="bg-white shadow-sm">
                <div className="container mx-auto px-6 py-4">
                    <button onClick={() => onNavigate('home')} className="text-purple-600 hover:text-purple-800 font-semibold">&larr; Back to Home</button>
                </div>
            </header>
            <main className="container mx-auto px-6 py-12">
                <h1 className="text-3xl md:text-4xl font-bold text-gray-800 mb-8">Your Inbox</h1>
                <div className="bg-white p-6 rounded-lg shadow-lg">
                    {isLoading ? <p>Loading conversations...</p> : (
                        <div className="space-y-4">
                            {conversations.length === 0 ? <p>You have no conversations yet.</p> :
                                conversations.map(convo => (
                                    <div 
                                        key={convo.id} 
                                        onClick={() => onNavigate('chat', { conversationId: convo.id })}
                                        className="p-4 border rounded-lg hover:bg-gray-50 cursor-pointer"
                                    >
                                        <div className="flex justify-between items-center">
                                            <div>
                                                <p className="font-bold text-lg">{getOtherParticipantName(convo)}</p>
                                                <p className="text-sm text-gray-600">Regarding: {convo.listingTitle}</p>
                                            </div>
                                            <p className="text-xs text-gray-400">{new Date(convo.updatedAt._seconds * 1000).toLocaleString()}</p>
                                        </div>
                                        <p className="text-sm text-gray-500 mt-2 truncate">{convo.lastMessage}</p>
                                    </div>
                                ))
                            }
                        </div>
                    )}
                </div>
            </main>
        </div>
    );
}

export default InboxPage;
